import Grid from "@mui/material/Grid";
import Logo from "../imgs/full-logo.png";
import Box from "@mui/material/Box";

import "./LoginPage.css";
import theme from "../ui/Theme";
import { ThemeProvider } from "@mui/material/styles";
import { Typography } from "@mui/material";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import {useEffect, useState} from 'react'

function LoginPage() {

    const navigate = useNavigate();
    const [chats,setChats] = useState([])
    const handleRouteSignUp = () =>{ 
      navigate("/signup");
    }

    const handleRouteHome = () =>{ 
        navigate("/main");
      }

    const fetchChats = async() =>{
        const data = await axios.get("api/chats")
        setChats(data)

    }
    useEffect(() =>{
      fetchChats();
    },[])


  return (
    // <div>
    //   {chats.map((chat) => (
    //     <div>{chat.chatName}</div>
    // ))}</div>
    <ThemeProvider theme={theme}>
      <Box class="background-login">
        <Grid container>
          <Grid xs={4}>
          </Grid>
          <Grid xs={4}>
            <Typography variant="h4">
              <Box
                sx={{
                  textAlign: "center",
                  marginTop: "5rem",
                  backgroundColor: "secondary.main",
                  padding: "20px",
                }}
              >
                <img class="logo" src={Logo} alt="brand logo" />
                <div>
                  <input
                    class="login-text"
                    type="text"
                    placeholder="Email, or username"
                  />
                  <input
                    class="login-text"
                    type="password"
                    placeholder="Password"
                  />
                  <button class="login-btn" onClick={handleRouteHome}>Log in</button>
                </div>
              </Box>

              <Box
                sx={{
                  textAlign: "center",
                  marginTop: "0.5rem",
                  backgroundColor: "secondary.main",
                  padding: "20px"
                }}
              >
                <Typography variant="h3" fontSize="1rem">
                  Dont' have an account ?{" "}
                </Typography>
                <div className="sign_up_button" onClick={handleRouteSignUp}>
                <Typography variant="h3" fontSize="1rem" color="primary.main" >
                  Sign Up
                </Typography>
                </div>

              </Box>
            </Typography>
          </Grid>
          <Grid xs={4}>
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default LoginPage;
